import React, {useState, PropsWithChildren} from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import StyledProps from '../props/StyledProps';
import SkillsCardProps from '../props/SkillsCardProps';
import {withStyles, createStyles} from '@material-ui/styles';
import {useSpring, animated} from 'react-spring';

const SkillsCard = (props: PropsWithChildren<StyledProps> & SkillsCardProps) => {
    const classes = props.classes;
    const [cardShadow, setCardShadow] = useState(5);
    const onMouseOver = () => setCardShadow(20);
    const onMouseOut = () => setCardShadow(5);

    const calc = (x : any, y : any) => [-(y - window.innerHeight / 2) / 20, (x - window.innerWidth / 2) / 20, 1.1]
    const trans = (x : any, y : any, s : any) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`

    const [set] = useSpring(() => ({ xys: [0, 0, 1], config: { mass: 5, tension: 350, friction: 40 } }))

    return(
        <Grid item xs={3}>
            <animated.div
            onMouseMove={({ clientX: x, clientY: y }) => set({ xys: calc(x, y) })}
            onMouseLeave={() => set({ xys: [0, 0, 1] })}
            style={{ transform: props.xys.interpolate(trans) }}
                >
                <CardContent>
                    <img className={classes.icon} src={props.icon} alt="reacticon"/>
                    <Divider/>
                    <Typography className={classes.text}>{props.title}</Typography>
                    {props.children}
                </CardContent>
            </animated.div>
        </Grid>
    );
}

const useStyles = createStyles({
    text: {
      fontFamily: 'Montserrat',
      color: '#12130F',
      fontSize: '1.5rem',
      textAlign: 'center',
      padding: '1rem',
    },
    icon: {
      height: '40%',
      width: '40%',
    },
  });

  export default withStyles(useStyles)(SkillsCard);
