import React from 'react';
import './css/About.css';
import lonelyfox from './static/avatar.png';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import {Link} from 'react-router-dom';
import Avatar from '@material-ui/core/Avatar';
import {withStyles, createStyles} from '@material-ui/styles';
import {useSpring, animated} from 'react-spring';
import StyledProps from './StyledProps';

const Navigation = (props: StyledProps) => {
  const classes = props.classes;
  const [{textDecoration, textDecorationColor}, set] = useSpring(() => ({textDecoration: 'none', textDecorationColor: '#FEB954'}))
  return (   
      <Container className={classes.navBar}>
          <Box display={{xs: 'block', sm: 'flex', md: 'flex', lg: 'flex'}} className={classes.logoContainer}>
            <Avatar alt="Lonely Fox" src={lonelyfox} className={classes.avatar}></Avatar>
            <Link 
              to ="/" 
              className={classes.title}
              onMouseEnter={() => set({textDecoration: 'underline', textDecorationColor: '#FEB954'})}
              onMouseLeave={() => set({textDecoration: 'none', textDecorationColor: '#FEB954'})}
            >
              <animated.span style={{textDecoration}}>
                Nathanael Shim
              </animated.span>
            </Link>
          </Box>
          <Box display={{xs: 'grid', md: 'flex', lg: 'flex'}}>
              <Link to= "/" className={classes.text}> About </Link>
              <Link to= "/projects" className={classes.text}> Projects </Link>        
              <Link to= "/music" className={classes.text}>Music</Link>
          </Box>
      </Container>
  );
}

const useStyles = createStyles({
  navBar: {
    display: 'flex',
    alignItems: 'center',
    padding: '2rem',
  },
  logoContainer: {
    backgroundColor: '#F7F3F2',
    flexGrow: 1,
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
  text: {
    fontFamily: 'Montserrat',
    color: '#12130F',
    fontSize: '1.5rem',
    padding: '1rem',
  },
  title: {
    fontFamily: 'Montserrat',
    color: '#12130F',
    fontSize: '1.5rem',
    marginBlock:'2rem',
    paddingTop:'2rem',
    paddingLeft:'1rem',
    textDecoration: 'none',
    '&:hover': {
      textDecoration: 'underline',
      textDecorationColor: '#FEB954',
    }
  },
  avatar: {
    fontSize: '1rem',
    width: '100px',
    height: '100px',
  },
});

export default withStyles(useStyles)(Navigation);
