import React, {useState, PropsWithChildren} from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import StyledProps from '../props/StyledProps';
import ProjectCardProps from '../props/ProjectCardProps';
import {withStyles, createStyles} from '@material-ui/styles';
import {useSpring, animated} from 'react-spring';




const ProjectCard = (props: PropsWithChildren<StyledProps> & ProjectCardProps) => {
    const classes = props.classes;
    const calc = 1.05
    const trans : any = (s : any) : any => `perspective(1rem) scale(${s})`    
    const [properties, set] = useSpring(() => ({ s: 1, config: { mass: 5, tension: 700, friction: 40 } }))
    const[isShowing, setIsShowing] = useState(false);
    const [flipped, setFlipped] = useState(false);
    const { transform, opacity } = useSpring({
        opacity: flipped ? 1: 0,
        transform: `perspective(600px) rotateX(${flipped ? 180 : 0}deg)`,
        config: { mass: 5, tension: 500, friction: 80 }
    })

    return(
        <Grid item xs={4}>
            <div onClick={() => setFlipped(state => !state)}>
                <animated.div
                onMouseMove={() => set({ s: calc })}
                onMouseLeave={() => set({ s: 1 })}
                style={{ transform: properties.s.interpolate(trans) }}
                >
                    <Card className={classes.card}>
                        <div style={{position: 'relative'}} onMouseEnter={() => setIsShowing(true)} onMouseLeave={() => setIsShowing(false)}>
                            <Typography className={classes.text}>
                                {isShowing && (props.title)}
                            </Typography>
                            <CardMedia component="img" image={(props.background)} className={classes.picture}/>
                        </div>
                </Card>
                </animated.div>
                <animated.div style={{ opacity, transform: transform.interpolate(t => `${t} rotateX(180deg)`) }} />
            </div>
        </Grid>
    );
}

const useStyles = createStyles({
    text: {
      position: 'absolute',
      left: '40%', 
      fontFamily: 'Montserrat',
      color: '#12130F',
      fontSize: '2rem',
      textAlign: 'center',
      padding: '1rem',
    },
    picture: {
        height: '100%',
        width: '100%',
        opacity: 1,
        '&:hover': {
            opacity: .35,
            transitionDuration: '.5s',
        },
    },
    card: {
        position: 'relative',
        height: '100%',
        width: '100%',
    },
  });

  export default withStyles(useStyles)(ProjectCard);
