import React, {PropsWithChildren} from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import StyledProps from '../props/StyledProps';
import BlogCardProps from '../props/BlogCardProps';
import {withStyles, createStyles} from '@material-ui/styles';
import { Theme } from '@material-ui/core';

const BlogCard = (props: PropsWithChildren<StyledProps> & BlogCardProps) => {
    const classes = props.classes;
    return(
      <Grid item xs={12} sm={12} md={6} lg={3} style={{display: 'flex', justifyContent: 'center'}}>
            <Card className={classes.card}>
                <CardContent>
                    <Typography className={classes.text}>{props.title}</Typography>
                    <Divider/>
                    {props.children}
                </CardContent>
            </Card>
      </Grid>
    );
}

const useStyles = (theme: Theme) => createStyles({
    text: {
      fontFamily: 'Montserrat',
      color: '#12130F',
      fontSize: '1.5rem',
      textAlign: 'center',
      padding: '1rem',
      cursor: 'default',
      [theme.breakpoints.down('sm')]: {
        fontSize: '1.25rem',
      },
      [theme.breakpoints.down('md')]: {
        fontSize: '1.25rem',
      },
    },
    icon: {
      height: '10vw',
      width: '10vw',
    },
    card: {
      [theme.breakpoints.down('xs')]: {
        width: '60vw',
      },
    }
  });

  export default withStyles(useStyles)(BlogCard);
