            >
            <Card className={classes.card}>
                <CardContent>
                    <div style={{position: 'relative'}}>
                        <Typography className={classes.text}>
                            {props.title}
                        </Typography>
                        <CardMedia component="img" image={(props.background)} className={classes.picture}/>
                    </div>
                </CardContent>
            </Card>
            </animated.div>
        </Grid>
    );
}

const useStyles = createStyles({
    text: {
      position: 'absolute',
      left: '40%', 
      fontFamily: 'Montserrat',
      color: '#12130F',
      fontSize: '1.5rem',
      textAlign: 'center',
