import React from 'react';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import './css/About.css';
import Typography from '@material-ui/core/Typography';
import {withStyles, createStyles} from '@material-ui/styles';
import Navigation from './navigation';
import Contact from './contact';
import StyledProps from './StyledProps';
import Image from 'material-ui-image';
import {useRef, useEffect} from 'react';

const About = (props: StyledProps) => {
  const classes = props.classes;
  const nateshim = './static/nateshim.png';
  const imageRef = useRef<typeof Image>(null);
  useEffect(() => {
    // @ts-ignore
    if (!imageRef.current.state.imageLoaded) {
      // @ts-ignore
      imageRef.current.handleLoadImage();
    }
  }, [imageRef]);
  return (   
    <Box className={classes.backgroundContainer}>
      <Navigation/>
      <Container className={classes.section} maxWidth={false}>
        <Typography className={classes.bigText}>Hello,</Typography>
      </Container>
      <Container className={classes.bio} maxWidth={false}>
        <Image
          src="/static/nateshim.png"
          //@ts-ignore
          ref={imageRef}/>
        <Typography className={classes.text}>I am a web developer based in the Bay Area.</Typography>
      </Container>
      <Container className={classes.aboutMe} maxWidth={false}>
        <Box display={{xs: 'block', sm: 'flex', md: 'flex', lg: 'flex'}}>
          <Box className={classes.aboutMeBox}>
            <img className={classes.gif} src={require('./static/foxplayingpiano.gif')} alt="loading..."/>
            <Typography className={classes.text}>I like to play music.</Typography>
          </Box>
          <Box className={classes.aboutMeBox}>
            <img className={classes.gif} src={require('./static/foxcomputer.gif')} alt="loading..."/>
            <Typography className={classes.text}>I like to code.</Typography>
          </Box>
          <Box className={classes.aboutMeBox}>
            <img className={classes.gif} src={require('./static/foxdrawing.gif')} alt="loading..."/>
            <Typography className={classes.text}>I like to draw.</Typography>
          </Box>
        </Box>
      </Container>
      <Contact/>
    </Box> 
  );
}

const useStyles = createStyles({
  section: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#F7F3F2',
    alignItems: 'center',
    padding: '15rem 0',

  },
  bio: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#8F8073',
    alignItems: 'center',
    padding: '10rem 0',
  },
  aboutMe: {
    display: 'flex',
    flexDirection: 'row',
    backgroundColor: '#FEB954',
    justifyContent: 'center',
    padding: '4rem 0',
  },
  aboutMeBox: {
    padding: '10rem',
  },
  text: {
    fontFamily: 'Colombo',
    color: '#12130F',
    fontSize: '1.5rem',
    textAlign: 'center',
    padding: '2rem',
  },
  bigText: {
    fontFamily: 'Colombo',
    color: '#12130F',
    fontSize: '4rem',
    textAlign: 'center',
    paddingBottom: '15rem',
    fontWeight: 'bold',
  },
  gif: {
    height: '200px',
    width: '200px',
  },
  bioTitle: {
    fontFamily: 'Colombo',
    color: '#12130F',
    fontSize: '3rem',
    padding: '1rem',
  },
  backgroundContainer: {
    backgroundSize: 'cover',
    height: '100%',
    width: '100%',
    backgroundColor: '#F7F3F2',
    backgroundPosition: 'center',
  },
  avatar: {
    height: '300px',
    width: '300px',
  },
});

export default withStyles(useStyles)(About);
