import React from 'react';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Avatar from '@material-ui/core/Avatar';
import './css/About.css';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import {withStyles, createStyles} from '@material-ui/styles';
import Navigation from './navigation';
import Contact from './contact';
import StyledProps from './StyledProps';
import nateshim from './static/nateshim.png';
import {WindupChildren, Pace} from 'windups';
import AOS from 'aos';
import 'aos/dist/aos.css';
import react from './static/react.png';
import node from './static/nodejs.png';
import node2 from './static/nodejs2.png';
import Divider from '@material-ui/core/Divider';

const About = (props: StyledProps) => {
  const classes = props.classes;

  AOS.init();
  return (   
    <Box id="nateshim" className={classes.backgroundContainer}>
      <Navigation/>
      <Container className={classes.section} maxWidth={false}>
        <WindupChildren>
          <span className={classes.bigText}>
            <Pace ms={85}>{"        hello."}</Pace>
          </span>
        </WindupChildren>
      </Container>
      <Container id="about" className={classes.bio} maxWidth={false}>
        <Box display={{xs: 'block', sm: 'flex', md: 'flex', lg: 'flex'}}>
          <Avatar data-aos="fade-right" data-aos-delay='300' data-aos-duration='1000' src={nateshim} className={classes.avatar}/>
          <Box data-aos="fade-left" data-aos-delay='300' data-aos-duration='1000' style={{backgroundColor: '#F7F3F2'}}>
            <Container style={{padding: '4rem'}}>
              <Typography variant="subtitle1" className={classes.text}>I am a web developer currently based in the Bay Area who loves to create and collaborate with others in producing user friendly software. When I'm not sitting at my desktop, I'm usually making music, jotting down pixels, or hanging out with friends.</Typography>
            </Container>
          </Box>
        </Box>
      </Container>
      <Box className={classes.recentWork}>
          <Typography className={classes.recentWorkTitle}>Skills</Typography>
          <Grid 
            container
            spacing={2}
            direction="row"
            justify="center"
            alignItems="center"
            >
            <Grid item xs={3}>
              <Card raised={true}>
                <CardContent>
                  <img className={classes.icon} src={react} alt="reacticon"/>
                  <Divider/>
                  <Typography className={classes.text}>React</Typography>
                </CardContent>
              </Card>
            </Grid>
            <Grid item xs={3}> 
              <Card raised={true}>
                <CardContent>
                  <img className={classes.icon} src={node2} alt="nodeicon"/>
                <Divider/>
                <Typography className={classes.text}>Node.js</Typography>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
      </Box>
      <Contact/>
    </Box> 
  );
}

const useStyles = createStyles({
  section: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#F7F3F2',
    alignItems: 'center',
    padding: '15rem 0',

  },
  bio: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#FEB954',
    alignItems: 'center',
    padding: '10rem 0',
  },
  text: {
    fontFamily: 'Montserrat',
    color: '#12130F',
    fontSize: '1.5rem',
    textAlign: 'center',
    padding: '2rem',
  },
  bigText: {
    fontFamily: 'Colombo',
    color: '#12130F',
    fontSize: '4rem',
    textAlign: 'center',
    paddingBottom: '15rem',
    fontWeight: 'bold',
  },
  recentWorkTitle: {
    fontFamily: 'Montserrat',
    color: '#12130F',
    fontSize: '3rem',
    padding: '2rem',
  },
  recentWork: {
    textAlign: 'center',
    display: 'block',
    padding: '2rem',
    backgroundColor: '#FEB954',

  },
  backgroundContainer: {
    backgroundSize: 'cover',
    height: '100%',
    width: '100%',
    backgroundColor: '#F7F3F2',
    backgroundPosition: 'center',
  },
  avatar: {
    height: '300px',
    width: '300px',
    marginRight: '3rem',
  },
  icon: {
    height: '100px',
    width: '100px',
  }
});

export default withStyles(useStyles)(About);
