import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Box from '@material-ui/core/Box';
import Button from '@material-ui/core/Button';
import Icon from '@material-ui/core/Icon';
import { Theme } from '@material-ui/core';
import Container from '@material-ui/core/Container';
import Drawer from '@material-ui/core/Drawer';
import RouterLink from './components/RouterLink';



class StyledAppBar extends React.Component {
    menuItems = [
      {
        label: 'about',
        path: '/about',
      },
      {
        label: 'ministries',
        path: '/ministries',
      },
      {
        label: 'missions',
        path: '/missions',
      },
      {
        label: 'sermons',
        path: '/sermons',
      },
      {
        label: 'resources',
        path: '/resources',
      },
      {
        label: 'members',
        path: '/members',
      },
    ];

    constructor(props) {
      super(props);
      this.state = {
        showDrawer: false,
      };
    }

    getMenuItems = () => {
      return this.menuItems.map((item) => {
        return (
          <RouterLink to={item.path} className={this.props.classes.menuItem}>
            {item.label}
          </RouterLink>
        );
      });
    };

    toggleDrawer(show) {
      this.setState({ showDrawer: show });
    }

    render() {
      const classes = this.props.classes;

      return (
        <AppBar className={classes.root} position="sticky">
          <Container>
            <Toolbar disableGutters>
              <Box className={classes.logoContainer}>
                <RouterLink to="/" className={classes.logo}>
                  EBCB
                </RouterLink>
              </Box>
              <Box display={{ xs: 'none', lg: 'block' }}>{this.getMenuItems()}</Box>
              <Box display={{ xs: 'block', lg: 'none' }}>
                <Button className={classes.icon} onClick={() => this.toggleDrawer(true)}>
                  <Icon>menu</Icon>
                </Button>
              </Box>
            </Toolbar>
          </Container>
          <Drawer
            classes={{ paper: classes.drawer }}
            anchor="right"
            open={this.state.showDrawer}
            onClose={() => this.toggleDrawer(false)}
          >
            <Box className={classes.drawerContent}>
              <Icon color="inherit" onClick={() => this.toggleDrawer(false)}>
                close
              </Icon>
              {this.getMenuItems()}
            </Box>
          </Drawer>
        </AppBar>
      );
    }
  }


  export default (StyledAppBar);
