import React, {useState, PropsWithChildren} from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import StyledProps from '../props/StyledProps';
import SkillsCardProps from '../props/SkillsCardProps';
import {withStyles, createStyles} from '@material-ui/styles';

const SkillsCard = (props: PropsWithChildren<StyledProps> & SkillsCardProps) => {
    const classes = props.classes;
    const [cardShadow, setCardShadow] = useState(5);
    const onMouseOver = () => setCardShadow(20);
    const onMouseOut = () => setCardShadow(5);
    return(
        <Grid item xs={3}>
            <Card 
                raised={true}
                onMouseOver={onMouseOver}
                onMouseOut={onMouseOut}
                elevation={cardShadow}
                >
                <CardContent>
                    <img className={classes.icon} src={props.icon} alt="reacticon"/>
                    <Divider/>
                    <Typography className={classes.text}>{props.title}</Typography>
                    {props.children}
                </CardContent>
            </Card>
        </Grid>
    );
}

const useStyles = createStyles({
    text: {
      fontFamily: 'Montserrat',
      color: '#12130F',
      fontSize: '1.5rem',
      textAlign: 'center',
      padding: '1rem',
    },
    icon: {
      height: '40%',
      width: '40%',
    },
  });

  export default withStyles(useStyles)(SkillsCard);
